mapper.select:
select
  comment_id, mid, uid, reply_uid, content
from comment


        and comment_id = #{commentId}


        and mid = #{mid}


        and uid = #{uid}


        and reply_uid = #{replyUid}


        and content = #{content}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from comment


        and comment_id = #{commentId}


        and mid = #{mid}


        and uid = #{uid}


        and reply_uid = #{replyUid}


        and content = #{content}
