mapper.select:
select
  cid, score, location, cname, ccode
from country


        and cid = #{cid}


        and score = #{score}


        and location = #{location}


        and cname = #{cname}


        and ccode = #{ccode}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from country


        and cid = #{cid}


        and score = #{score}


        and location = #{location}


        and cname = #{cname}


        and ccode = #{ccode}

mapper.update:
update country


        score = #{score},


        location = #{location},


        cname = #{cname},


        and ccode = #{ccode}


where cid = #{cid}
