mapper.select:
select
  did, cid, dname, ddescription, daddress, dphone
from department


        and did = #{did}


        and cid = #{cid}


        and dname = #{dname}


        and ddescription = #{ddescription}


        and daddress = #{daddress}


        and dphone = #{dphone}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from department


        and did = #{did}


        and cid = #{cid}


        and dname = #{dname}


        and ddescription = #{ddescription}


        and daddress = #{daddress}


        and dphone = #{dphone}

mapper.update:
update department


        and cid = #{cid}


        dname = #{dname},


        ddescription = #{ddescription},


        daddress = #{daddress},


        and dphone = #{dphone}


where did = #{did}
