mapper.select:
select
  pid, pType, pContent, pTime, did, pTitle, pAbstract
from publication


        and pid = #{pid}


        and pType = #{ptype}


        and pContent = #{pcontent}


        and pTime = #{ptime}


        and did = #{did}


        and pTitle = #{ptitle}


        and pAbstract = #{pabstract}


limit #{pageable.offset}, #{pageable.pageSize}

select
  pid, pType, pContent, pTime, did, pTitle, pAbstract
from publication


        and pid = #{pid}


        and pType = #{ptype}


        and pContent = #{pcontent}


        and pTime = #{ptime}


        and did = #{did}


        and pTitle = #{ptitle}


        and pAbstract = #{pabstract}

select count(1)
from publication


        and pid = #{pid}


        and pType = #{ptype}


        and pContent = #{pcontent}


        and pTime = #{ptime}


        and did = #{did}


        and pTitle = #{ptitle}


        and pAbstract = #{pabstract}
