mapper.select:
select
  pid, pType, pContent, pAttach, pTime, pPic
from publication


        and pid = #{pid}


        and pType = #{ptype}


        and pContent = #{pcontent}


        and pAttach = #{pattach}


        and pTime = #{ptime}


        and pPic = #{ppic}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from publication


        and pid = #{pid}


        and pType = #{ptype}


        and pContent = #{pcontent}


        and pAttach = #{pattach}


        and pTime = #{ptime}


        and pPic = #{ppic}
