mapper.select:
select
  pid, did, pType, pContent, pTime, pTitle
from publication


        and pid = #{pid}


        and did = #{did}


        and pType = #{ptype}


        and pContent = #{pcontent}


        and pTime = #{ptime}


        pTitle = #{ptitle},


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from publication


        and pid = #{pid}


        and did = #{did}


        and pType = #{ptype}


        and pContent = #{pcontent}


        and pTime = #{ptime}


        pTitle = #{ptitle},
