mapper.select:
select
  uid, status, uname, ugender, uage, uaddress, uwxid, uphoto, uphone, ucreate_time
from user


        and uid = #{uid}


        and status = #{status}


        and uname = #{uname}


        and ugender = #{ugender}


        and uage = #{uage}


        and uaddress = #{uaddress}


        and uwxid = #{uwxid}


        uphoto = #{uphoto},


        uphone = #{uphone},


        and ucreate_time = #{uCreateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and uid = #{uid}


        and status = #{status}


        and uname = #{uname}


        and ugender = #{ugender}


        and uage = #{uage}


        and uaddress = #{uaddress}


        and uwxid = #{uwxid}


        uphoto = #{uphoto},


        uphone = #{uphone},


        and ucreate_time = #{uCreateTime}
